INSERT INTO `t_title` VALUES ('6', '解决“碎片”问题最好的存储管理方法是 ( ) 。', 'A.页面存储管理 ,B.段式存储管理 ,C.多重分区管理 ,D.可变分区管理', 'D', '2', '2', '离散数学');
INSERT INTO `t_title` VALUES ('7', '在页式存储管理方案中，采用 ( ) 实现地址变换　', 'A.页表 ,B.段表 ,C.段表和页表 ,D.空闲区表', 'A', '2', '2', '离散数学');
INSERT INTO `t_title` VALUES ('8', '资源的有序分配算法在解决死锁问题中是用于 ( ) ', 'A.预防死锁 ,B.避免死锁 ,C.检测死锁 ,D.解除死锁', 'B', '2', '2', '离散数学');
INSERT INTO `t_title` VALUES ('9', '与文件保护无关的操作是（ ）。', 'A.口令机制 　,B.数据加密技术 ,C.访问控制表 ,D.访问前执行open操作，访问后执行close操作  ', 'D', '2', '2', '离散数学');
INSERT INTO `t_title` VALUES ('10', ' 在许多系统中，用户是否能对某个文件进行相应的访问，将受（） 的共同限制。', 'A.用户优先级和访问控制表 ,B.用户优先级和访问权限表 ,C.用户优先级和文件口令 ,D.访问控制表和访问权限表', 'B', '2', '2', '离散数学');
INSERT INTO `t_title` VALUES ('11', ' 为记录设备的情况，系统为每一类设备配置一张 （ ）。　', 'A.设备控制表 ,B.逻辑设备表 ,C.设备分配表 ,D.设备开关表  ', 'C', '3', '2', '离散数学');
INSERT INTO `t_title` VALUES ('12', '在请求分页系统中，主要的硬件支持有请求分页的页表机制、缺页中断机构和（ ）。　', 'A.时间支持,B.空间支持 ,C.地址变换机构 ,D.虚拟存储 ', 'C', '3', '2', '离散数学');
INSERT INTO `t_title` VALUES ('13', '一进程在获得资源后，只能在使用完资源时由自己释放，这属于死锁必要条件的（ ）。', 'A.互斥条件 ,B.请求和释放条件, C 不剥夺条件 ,D 环路等待条件  ', 'C', '3', '2', '离散数学');
